{"changed":true,"filter":false,"title":"stylist_controller.rb","tooltip":"/app/controllers/stylist_controller.rb","value":"class StyleController < ApplicationController\n  before_action :set_stylist, except: [:index, :new, :create]\n  before_action :authenticate_user!, except: [:show]\n  before_action :is_authorized, only: [:listing, :pricing, :description, :photo_upload, :amenities, :location, :update]\n\n  def index\n    @style = current_user.stylist\n  end\n\n  def new\n        @style = current_user.stylists.build\n  end\n\n  def create\n    style = current_user.styles.build(style)\n    if @stylist.save\n      redirect_to listing_stylist_path(@style), notice: \"Saved...\"\n    else\n      flash[:alert] = \"Something went wrong...\"\n      render :new\n    end\n  end\n  \n  def show\n    @photos = @stylist.photos\n    @client_reviews = @stylist.client_reviews\n  end\n  \n  def listing\n    \n  end\n\n  def pricing\n  end\n\n  def description\n  end\n\n  def photo_upload\n  end\n\n  def amenities\n  end\n\n  def location\n  end\n\n  def update\n    new_params = stylist_params\n    new_params = stylist_params.merge(active: true) if is_ready_stylist\n\n    if @stylist.update(new_params)\n      flash[:notice] = \"Saved...\"\n    else\n      flash[:alert] = \"Something went wrong...\"\n    end\n    redirect_back(fallback_location: request.referer)\n  end\n  \n  private\n    def is_conflict(start_date, end_date, stylist)\n      check = stylist.reservations.where(\"? < start_date AND end_date < ?\", start_date, end_date)\n      check.size > 0? true : false\n    end\n\n    def set_stylist\n      @stylist = Stylist.find(params[:id])\n    end\n\n    def is_authorised\n      redirect_to root_path, alert: \"You don't have permission\" unless current_user.id == @stylist.user_id\n    end\n\n    def is_ready_stylist\n      !@stylist.active && !@stylist.price.blank? && !@stylist.listing_name.blank? && !@stylist.photos.blank? && !@stylist.address.blank?\n    end\n\n    def stylist